var finalText="";$(function(){$('[data-action="Encode"]').click(function(){$("#error").empty();try{finalText=btoa($("#Source").val()),$("#Source").val(finalText)}catch(r){$("#error").html("<b>Error encodig</b>")}}),$('[data-action="Decode"]').click(function(){$("#error").empty();try{finalText=atob($("#Source").val()),$("#Source").val(finalText)}catch(r){$("#error").html("<b>Error decoding</b>")}}),$('[data-action="ToHex"]').click(function(){$("#error").empty();try{finalText=btoa($("#Source").val());var r=atob(finalText),t="";for(i=0;i<r.length;i++){var o=r.charCodeAt(i).toString(16);t+=2==o.length?o:"0"+o}$("#Source").val(t.toUpperCase())}catch(r){$("#error").html("<b>Error encodig</b>")}}),$('[data-action="ToText"]').click(function(){$("#error").empty();try{for(var r=$("#Source").val(),t="",o=0;o<r.length;o+=2)t+=String.fromCharCode(parseInt(r.substr(o,2),16));$("#Source").val(t)}catch(r){$("#error").html("<b>Error encodig</b>")}})});